VanillaTilt.init(document.querySelectorAll(".image-comparison-slider"), { // Tilt Effect - vanilla-tilt.js (https://micku7zu.github.io/vanilla-tilt.js/) is required for this
    max: 5, // max tilt rotation (degrees (deg))
    speed: 800, // speed (transition-duration) of the enter/exit transition (milliseconds (ms))
    scale: 1.02 // transform scale - 2 = 200%, 1.5 = 150%, etc..
  });
  

  var slider = document.querySelectorAll(".image-comparison-slider");
  var sliderImgWrapper = document.querySelectorAll(".image-comparison-slider .img-wrapper");
  var sliderHandle = document.querySelectorAll(".image-comparison-slider .handle");

  for (var i = 0; i < slider.length; i++) {
    slider[i].addEventListener("mousemove", sliderMouseMove);
    slider[i].addEventListener("touchmove", sliderMouseMove);
  }
 

  //slider.addEventListener("mousemove", sliderMouseMove);
  //slider.addEventListener("touchmove", sliderMouseMove);
  
  
  function sliderMouseMove(event) {
    for (var i = 0; i < slider.length; i++) {
    if(isSliderLocked) return;
  
    const sliderLeftX = slider[i].offsetLeft;
    const sliderWidth = slider[i].clientWidth;
    const sliderHandleWidth = sliderHandle[i].clientWidth;
  
    let mouseX = (event.clientX || event.touches[0].clientX) - sliderLeftX;
    if(mouseX < 0) mouseX = 0;
    else if(mouseX > sliderWidth) mouseX = sliderWidth;
  
       sliderImgWrapper[i].style.width = `${((1 - mouseX/sliderWidth) * 100).toFixed(4)}%`;   
     
       sliderHandle[i].style.left = `calc(${((mouseX/sliderWidth) * 100).toFixed(4)}% - ${sliderHandleWidth/2}px)`;
    }
  }
  
  let isSliderLocked = false;
  
  
  for (var i = 0; i < slider.length; i++) {
  slider[i].addEventListener("mousedown", sliderMouseDown);
  slider[i].addEventListener("touchstart", sliderMouseDown);
  slider[i].addEventListener("mouseup", sliderMouseUp);
  slider[i].addEventListener("touchend", sliderMouseUp);
  slider[i].addEventListener("mouseleave", sliderMouseLeave);
  }
  function sliderMouseDown(event) {
    if(isSliderLocked) isSliderLocked = false;
    sliderMouseMove(event);
  }
  
  function sliderMouseUp() {
    if(!isSliderLocked) isSliderLocked = true;
  }
  
  function sliderMouseLeave() {
    if(isSliderLocked) isSliderLocked = false;
  }
